<?phprequire_once("../includes/initialize.php");if (!$session->is_logged_in()) { redirect_to("index.php"); }$per_page = 50;$page =!empty($_GET['page']) ? (int)$_GET['page'] : 1;if (isset($_POST['submit'])) { // Form has been submitted.  $abbcode = trim($_POST['abbcode']);  $abbname = trim($_POST['abbname']);    $refsql = Abbreviations::abb_sql_where($abbcode, $abbname);}     else { // Form has not been submitted.  $abbcode = "";  $abbname = "";  if(!empty($_GET['sql'])) {  $refsql = stripslashes($_GET['sql']); }   else $refsql = "";}$total_count = Abbreviations::count_in_sql($refsql);$pagination = new Pagination($page, $per_page, $total_count);$found_records = Abbreviations::find_by_sql_lim($refsql, $per_page, $pagination->offset());?><?php include_layout_template('header.php'); ?><ul id="navlinks">		<li><a href="index.php">User Login</a></li>		<li><a href="refsearch.php"> References</a></li>		<li><a href="datasearch.php"> Data</a></li>		<li><a href="keysearch.php"> Keywords</a></li>		<li><a href="isosearch.php"> Isotopes</a></li>		<li> Abbreviations</li>			</ul><div id="leftcol">		<form action="abbsearch.php" method="post">		  <table>		    <tr>		      <td>Abbcode:</td>		      <td>		        <input type="text" name="abbcode" size="14" maxlength="5" value="<?php echo htmlentities($abbcode); ?>" />		      </td>		    </tr>		    <tr>		      <td>Abbname:</td>		      <td>		        <input type="text" name="abbname" size="14" maxlength="30" value="<?php echo htmlentities($abbname); ?>" />		      </td>		    </tr>			    <tr>		      <td colspan="2">		        <input type="submit" name="submit" value="Search" />		      </td>		    </tr>		  </table>		</form><?php 	$current_page = $pagination->next_page()-1;	echo "Found - ". $total_count . "<br/>Page ". $current_page ." out of ". $pagination->total_pages() . "<br/>";	if($pagination->total_pages() > 1) {			if($pagination->has_previous_page()){			echo " <a href=\"abbsearch.php?page=";			echo $pagination->previous_page();			echo "&sql=".$refsql."\">&laquo; Previous</a> ";		}			if($pagination->has_next_page()){			echo " <a href=\"abbsearch.php?page=";			echo $pagination->next_page();			echo "&sql=".$refsql."\">Next &raquo; </a> ";		}		}?></div>	<div id="data"><?php 	if($total_count > 0) { foreach($found_records as $record) echo $record->code_name() ."<br/>";	}		else {		echo "Tips for searching Abbreviations: </br>		Enter an abbreviation code in  Abbcode box to check out the meaning<br/>		for example: thf<br/>		enter any word in Abbname box to find matching abbreviation codes<br/>		for example: tetrahydrofuran<br/>";				}?></div><div id="pagination" style="clear: both;"></div><div id="searchblock" style="clear: both;"></div><?php include_layout_template('footer.php'); ?>