<?phprequire_once('../../includes/initialize.php');if (!$session->is_admin()) { redirect_to("../index.php"); }$message = "";if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id'];	$user_record = user::find_rec_by_id($id);	}if (isset($_POST['save'])) { // save Form has been submitted.	$entry['first_name'] = trim($_POST['firstname']);	$entry['last_name'] = trim($_POST['lastname']);  	$entry['username'] = trim($_POST['username']);  	$entry['password'] = trim($_POST['password']); 	$entry['dateset'] = date('Y-m-j',time());	if(isset($_POST['id'])) {		$entry['id'] = $_POST['id']; 		$id = $_POST['id'];	}	else unset($entry['id']);	$user_record[0] = User::instantiateit($entry);			if($_POST['id'] == "") unset($user_record[0]->id);	$user_record[0]->save();	$message = "User ".$user_record[0]->username. " is saved.";}if (isset($_POST['delete'])) { // delete Form has been submitted.	$deluser = new User;	$deluser->id = $_POST['id'];	$deluser->delete();	$message = " User deleted";	$id ="";	$entry['first_name'] = "";	$entry['last_name'] = "";  	$entry['username'] = "";  	$entry['password'] = ""; 	$user_record[0] = User::instantiateit($entry);	}if (isset($_GET['newuser'])) {	$message = " Enter new user";	$id ="";	$entry['first_name'] = "";	$entry['last_name'] = "";  	$entry['username'] = "";  	$entry['password'] = ""; 	$user_record[0] = User::instantiateit($entry);	}?><?php include_layout_template('admin_header.php'); ?><ul id="navlinks">	<li><a href="../index.php"> User menu</a> </li>	<li><a href="listusers.php">List users</a></li>	<li><a href="edituser.php?newuser='new'" >New user</a></li>	<li><a href="fileupload.php" >File upload</a></li></ul><?php echo output_message($message); ?>				<form action="edituser.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>First name:</td>		      <td>		        <input type="text" name="firstname" maxlength="30" value="<?php echo htmlentities($user_record[0]->first_name); ?>" />		      </td>		    </tr>		    <tr>		      <td>Last Name:</td>		      <td>		        <input type="text" name="lastname" maxlength="30" value="<?php echo htmlentities($user_record[0]->last_name); ?>" />		      </td>		    </tr>		    <tr>		      <td>Username:</td>		      <td>		        <input type="text" name="username" maxlength="20" value="<?php echo htmlentities($user_record[0]->username); ?>" />		      </td>		    </tr>		    <tr>		      <td>Password:</td>		      <td>		        <input type="text" name="password" maxlength="20" value="<?php echo htmlentities($user_record[0]->password); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="2">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="2">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form><?php include_layout_template('admin_footer.php'); ?>		