<?phprequire_once("../includes/initialize.php");if (!$session->is_logged_in()) { redirect_to("index.php"); }$per_page = 50;$page =!empty($_GET['page']) ? (int)$_GET['page'] : 1;if (isset($_POST['submit'])) { // Form has been submitted.  $isocode = trim($_POST['isocode']);  $isoname = trim($_POST['isoname']);     $refsql = Isotopes::iso_sql_where($isocode, $isoname);}     else { // Form has not been submitted.  $isocode = "";  $isoname = "";  if(!empty($_GET['sql'])) {  $refsql = stripslashes($_GET['sql']); }   else $refsql = "";}$total_count = Isotopes::count_in_sql($refsql);$pagination = new Pagination($page, $per_page, $total_count);$found_records = Isotopes::find_by_sql_lim($refsql, $per_page, $pagination->offset());?><?php include_layout_template('header.php'); ?><ul id="navlinks">		<li><a href="index.php">User Login</a></li>		<li><a href="refsearch.php"> References</a></li>		<li><a href="datasearch.php"> Data</a></li>		<li><a href="keysearch.php"> Keywords</a></li>		<li> Isotopes</li>		<li><a href="abbsearch.php"> Abbreviations</a></li>			</ul><div id="leftcol">		<form action="isosearch.php" method="post">		  <table>		    <tr>		      <td>Isotope code:</td>		      <td>		        <input type="text" name="isocode" size="11" maxlength="3" value="<?php echo htmlentities($isocode); ?>" />		      </td>		    </tr>		    <tr>		      <td>Isotope:</td>		      <td>		        <input type="text" name="isoname" size="11" maxlength="6" value="<?php echo htmlentities($isoname); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="2">		        <input type="submit" name="submit" value="Search" />		      </td>		    </tr>		  </table>		</form><?php 	$current_page = $pagination->next_page()-1;	echo "Found - ". $total_count . "<br/>Page ". $current_page ." out of ". $pagination->total_pages() . "<br/>";	if($pagination->total_pages() > 1) {			if($pagination->has_previous_page()){			echo " <a href=\"isosearch.php?page=";			echo $pagination->previous_page();			echo "&sql=".$refsql."\">&laquo; Previous</a> ";		}			if($pagination->has_next_page()){			echo " <a href=\"isosearch.php?page=";			echo $pagination->next_page();			echo "&sql=".$refsql."\">Next &raquo; </a> ";		}		}?></div>		<div id="data"><?php 	if($total_count > 0) 	foreach($found_records as $record){	echo $record->code_name() ."<br/>"; }	else {		echo "Tips for searching Isotopes: </br>		Enter a isotope code in  Isotope code box to check out the meaning<br/>		for example G52<br/>		enter any isotope in Isotope box to find matching isotope codes<br/>		for example Gd-155<br/>";				}?></div><div id="pagination" style="clear: both;"></div><div id="searchblock" style="clear: both;"></div><?php include_layout_template('footer.php'); ?>